import 'package:dentist_flutter/models/recipe..card.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class DescriptionPage extends StatefulWidget {  const DescriptionPage({Key? key, required this.data}) : super(key: key);  final Reciapp data;  @override  _DescriptionPageState createState() => _DescriptionPageState();}class _DescriptionPageState extends State<DescriptionPage> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text(widget.data.title),      ),      body: ListView(        children: [          Image.network(            widget.data.thumbnailUrl,            width: 600,            height: 240,            fit: BoxFit.cover,          ),          Container(            padding: const EdgeInsets.all(32),            child: Row(              children: [                Expanded(                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      Container(                        padding: const EdgeInsets.only(bottom: 8),                        child: Text(                          'Название: ${widget.data.title}',                          style: const TextStyle(                            color: Colors.black,                            fontWeight: FontWeight.bold,                          ),                        ),                      ),                      Text(                        'Время готовки: ${widget.data.cookTime}',                        style: TextStyle(                          color: Colors.grey[500],                        ),                      )                    ],                  ),                ),                const Icon(                  Icons.star,                  color: Colors.yellow,                ),                Text(                  widget.data.rating,                  style: const TextStyle(color: Colors.black),                ),              ],            ),          ),          buttonSection,          textSection        ],      ),    );  }  Widget buttonSection = Row(    mainAxisAlignment: MainAxisAlignment.spaceEvenly,    children: [      _buildButtonColumn(Colors.blueAccent, Icons.call, 'Позвонить'),      _buildButtonColumn(Colors.blueAccent, Icons.near_me, 'Написать'),      _buildButtonColumn(Colors.blueAccent, Icons.share, 'Поделиться'),    ],  );  Widget textSection = const Padding(    padding: EdgeInsets.all(32),    child: Text(      'Если есть в мире понятная нам и одновременно интересная кухня, то это, несомненно, -'      'американская. Она не ограничена вредными бургерами и картошкой фри, как считают многие. Здесь'      'представлены с одной стороны очень доступные, а с другой – питательные, полезные и вкусные '      "продукты. Способы приготовления и добавляемые специи разнятся от региона к региону, но есть"      'кое-что объединяющее. Американские блюда – это, прежде всего, традиции. Что бы ни диктовали'      'кулинарные тенденции, некоторые рецептуры остаются здесь совершенно неизменными.',      softWrap: true,      style: TextStyle(color: Colors.black),    ),  );}Column _buildButtonColumn(Color color, IconData icon, String label) {  return Column(    mainAxisAlignment: MainAxisAlignment.center,    mainAxisSize: MainAxisSize.min,    children: [      Icon(        icon,        color: color,      ),      Container(        margin: const EdgeInsets.only(top: 8),        child: Text(          label,          style: TextStyle(              fontSize: 13, fontWeight: FontWeight.w400, color: color),        ),      ),    ],  );}